mapper.select:
SELECT b.rnum, b.bat_grp_id as "batGrpId", b.bat_grp_nm as "batGrpNm", b.bat_grp_dsc as "batGrpDsc", b.host_id as "hostId", h.host_nm as "hostNm", h.host_ip as "hostIp", h.host_pt as "hostPt", b.auto_excn_yn as "autoExcnYn", b.cron as "cron", b.cron_dsc as "cronDsc" FROM ( SELECT ROWNUM as rnum, bat_grp_id, bat_grp_nm, bat_grp_dsc, host_id, auto_excn_yn, cron, cron_dsc, use_yn FROM (SELECT bat_grp_id, bat_grp_nm, bat_grp_dsc, host_id, auto_excn_yn, cron, cron_dsc, use_yn FROM bat_grp ORDER BY bat_grp_id DESC) WHERE rownum <= #{pager.endRowNo}
AND bat_grp_nm like '%'||#{vo.batGrpNm}||'%'
AND bat_grp_dsc like '%'||#{vo.batGrpDsc}||'%'
AND auto_excn_yn=#{vo.autoExcnYn}
AND (cron like '%'||#{vo.cron}||'%' OR cron_dsc like '%'||#{vo.cronDsc}||'%')
AND use_yn=#{vo.useYn} ) b LEFT OUTER JOIN host h ON b.host_id=h.host_id WHERE b.rnum >= #{pager.startRowNo}
AND (h.host_nm like '%'||#{vo.hostNm}||'%' OR h.host_ip like '%'||#{vo.hostNm}||'%' OR h.host_pt like '%'||#{vo.hostNm}||'%')

SELECT count(*) FROM ( SELECT bat_grp_id, bat_grp_nm, bat_grp_dsc, host_id, auto_excn_yn, cron, cron_dsc, use_yn FROM bat_grp WHERE use_yn=#{useYn}
AND bat_grp_nm like '%'||#{batGrpNm}||'%'
AND bat_grp_dsc like '%'||#{batGrpDsc}||'%'
AND auto_excn_yn=#{autoExcnYn}
AND (cron like '%'||#{cron}||'%' OR cron_dsc like '%'||#{cronDsc}||'%')
) b JOIN host h ON b.host_id=h.host_id
WHERE h.host_nm like '%'||#{hostNm}||'%' OR h.host_ip like '%'||#{hostNm}||'%' OR h.host_pt like '%'||#{hostNm}||'%'

  	SELECT count(*) 
  	FROM bat_prm 
	WHERE use_yn = 'Y' AND bat_grp_id = #{grpId}

mapper.update:
UPDATE
	bat_prm
SET
	excn_ord = #{excnOrd}
WHERE
	bat_prm_id = #{batPrmId}
