mapper.select:
SELECT rnum, host_id as hostId, host_ip as hostIp, host_pt as hostPt, host_nm as hostNm, use_yn as useYn FROM ( SELECT ROWNUM as rnum, host_id, host_ip, host_pt, host_nm, use_yn FROM ( SELECT host_id, host_ip, host_pt, host_nm, use_yn FROM host WHERE use_yn = #{host.useYn}
AND host_ip like '%'||#{host.hostIp}||'%'
AND host_nm like '%'||#{host.hostNm}||'%'
AND host_pt = #{host.hostPt}
ORDER BY host_id DESC ) WHERE rownum <= #{pager.endRowNo} ) WHERE rnum >= #{pager.startRowNo}

SELECT count(*) FROM host WHERE use_yn = #{useYn}
AND host_ip like '%'||#{hostIp}||'%'
AND host_nm like '%'||#{hostNm}||'%'
AND host_pt = #{hostPt}
