mapper.select:
SELECT count(*) FROM ( SELECT bat_grp_log_id, max(bat_grp_rty_cnt) FROM bat_grp_log
AND bat_grp_log_id like #{batGrpLogId}||'%'
AND bat_grp_id like '%'||#{batGrpId}||'%'
AND bat_grp_st_cd like '%'||#{batGrpStCd}||'%'
AND to_char(bat_bgng_dt, 'yyyy-mm-dd hh24:mi') >= #{batBgngDtStart}
AND to_char(bat_bgng_dt, 'yyyy-mm-dd hh24:mi') <= #{batBgngDtEnd}
AND to_char(bat_bgng_dt, 'yyyy-mm-dd hh24:mi') between #{batBgngDtStart} and #{batBgngDtEnd}
AND to_char(bat_end_dt, 'yyyy-mm-dd hh24:mi') between #{batEndDtStart} and #{batEndDtEnd}
AND to_char(bat_end_dt, 'yyyy-mm-dd hh24:mi') >= #{batEndDtStart}
AND to_char(bat_end_dt, 'yyyy-mm-dd hh24:mi') <= #{batEndDtEnd}
AND to_char(last_mdfcn_dttm, 'yyyymmdd') >= to_char(sysdate-7, 'yyyymmdd')
GROUP BY bat_grp_log_id ORDER BY bat_grp_log_id DESC )

SELECT rnum, bat_grp_log_id as batGrpLogId, bat_grp_rty_cnt as batGrpRtyCnt, bat_grp_id as batGrpId, bat_grp_st_cd as batGrpStCd, bat_bgng_dt as batBgngDt, bat_end_dt as batEndDt, bat_grp_nm as batGrpNm FROM ( SELECT ROWNUM as rnum, bat_grp_log_id, bat_grp_rty_cnt, bat_grp_id, bat_grp_st_cd, bat_bgng_dt, bat_end_dt, last_mdfr_nm, last_mdfcn_dttm, bat_grp_nm FROM ( SELECT l.bat_grp_log_id, l.bat_grp_rty_cnt, l.bat_grp_id, l.bat_grp_st_cd, l.bat_bgng_dt, l.bat_end_dt, l.last_mdfr_nm, l.last_mdfcn_dttm, bat_grp_nm FROM bat_grp_log l JOIN bat_grp g ON l.bat_grp_id = g.bat_grp_id WHERE (bat_grp_log_id, bat_grp_rty_cnt) IN (SELECT bat_grp_log_id, MAX(bat_grp_rty_cnt) FROM bat_grp_log GROUP BY (bat_grp_log_id))
AND bat_grp_log_id like '%'||#{log.batGrpLogId}||'%'
AND bat_grp_nm like '%'||#{log.batGrpNm}||'%'
AND bat_grp_st_cd like '%'||#{log.batGrpStCd}||'%'
AND to_char(bat_bgng_dt, 'yyyy-mm-dd hh24:mi') between #{log.batBgngDtStart} and #{log.batBgngDtEnd}
AND to_char(bat_bgng_dt, 'yyyy-mm-dd hh24:mi') >= #{log.batBgngDtStart}
AND to_char(bat_end_dt, 'yyyy-mm-dd hh24:mi') <= #{log.batBgngDtEnd}
AND to_char(bat_end_dt, 'yyyy-mm-dd hh24:mi') between #{log.batEndDtStart} and #{log.batEndDtEnd}
AND to_char(bat_end_dt, 'yyyy-mm-dd hh24:mi') >= #{log.batEndDtStart}
AND to_char(bat_end_dt, 'yyyy-mm-dd hh24:mi') <= #{log.batEndDtEnd}
AND to_char(l.last_mdfcn_dttm, 'yyyymmdd') >= to_char(sysdate-7, 'yyyymmdd')
ORDER BY
bat_bgng_dt, bat_grp_log_id
bat_bgng_dt DESC
bat_end_dt
bat_end_dt DESC
bat_grp_log_id DESC
, bat_grp_log_id DESC ) WHERE rownum <= #{pager.endRowNo} ) WHERE rnum >= #{pager.startRowNo}

SELECT COUNT(*) 
FROM bat_grp_log 
WHERE 
		TO_CHAR(bat_bgng_dt,'YY/MM/DD') = #{date}
	AND
		bat_grp_st_cd = #{code}
